mapper.insert:
INSERT INTO user_preferences (
    user_id, preferred_cuisine_types, preferred_price_range, preferred_area
) VALUES (
    #{userId}, #{preferredCuisineTypes}, #{preferredPriceRange}, #{preferredArea}
)

mapper.update:
UPDATE user_preferences SET
    preferred_cuisine_types = #{preferredCuisineTypes},
    preferred_price_range = #{preferredPriceRange},
    preferred_area = #{preferredArea}
WHERE user_id = #{userId}
